insert into Foods_EN(Food_ID,Food_Name,Description) values("acidulated water","acidulated water","Water to which a small amount of vinegar, lemon or lime juice has been added. It's used as a soak to prevent discoloration of some fruits and vegetables (such as apples and artichokes) that darken quickly when their cut surfaces are exposed to air. It can also be used as a cooking medium.");
