insert into Foods_EN(Food_ID,Food_Name,Description) values("agave","agave","Also called century plant, this family of succulents grows in the southwestern United States, Mexico and Central America. Though poisonous when raw, agave has a sweet, mild flavor when baked or made into a syrup. Certain varieties are used in making the alcoholic beverages mescal, pulque and tequila.");
