insert into Foods_EN(Food_ID,Food_Name,Description) values("agemono","agemono","A Japanese term referring to deep-frying (see deep-fry) and the foods produced from this cooking method. Tempura is the most famous of the Japanese foods cooked in this manner. Deep-frying is done in a pan called an <b>agemono-nabe</b>, which is similar to a Chinese wok.");
