insert into Foods_CN(Food_Title,CName,Description) values('Baked beans','烤的豆子','烤的豆子是包含豆，有时烤菜但，尽管名字，通常红烧，。 大多数商业罐头烤的豆子是由扁豆豆，也称为海军豆 — — 各式各样的菜豆的酱。在爱尔兰和英国，一个番茄和糖调味汁最常用的和他们通常被吃吐司或作为全套英式早餐的一部分。');
