insert into Foods_CN(Food_Title,CName,Description) values('Navy bean','海军豆','海军 bean 或扁豆是各种常见豆 （菜豆）。它是特别普遍在联合王国和美国 (在它可能也被称作豌豆 bean 它是俗称为"海军豆"由于它的用途作为主食的 19 世纪美国海军口粮。');
insert into Foods_CN(Food_Title,CName,Description) values('Green bean','绿豆','青豆，也称为四季豆、 豆角在美国东北部和西部或 ejotes 在墨西哥，有各种品种的普通菜豆 (菜豆) 的未成熟果实。 绿豆品种已选定尤其是为其后、 风味或其荚的甜蜜。');
insert into Foods_CN(Food_Title,CName,Description) values('Organic beans','有机黄豆','有机黄豆生产并没有合成化肥和农药的使用处理。2008 年，2,600,000 英亩 (11,000 km2) 退耕还林是有机认证在美国。 2');
insert into Foods_CN(Food_Title,CName,Description) values('Phaseolus vulgaris','菜豆','菜豆、 菜豆， 豆角是全球为其果实可食用的干种子或未熟的果子，这两个被称为豆玉米种植面积的年度草本植物。叶偶尔地也使用作为一种蔬菜，和一根稻草可以用于饲料。与其他种类的豆属 (绿豆)，它分为植物学豆科 (豆科)，大多数成员获得氮气通过协会与根瘤菌、 固氮的一种细菌。');
insert into Foods_CN(Food_Title,CName,Description) values('Frijoles negros','Frijoles 内格罗斯','Frijoles 内格罗斯 (逐字地"黑豆"在西班牙文中） 是有营养的菜用黑豆，准备在古巴、 波多黎各、 墨西哥和拉丁美洲的其他几个国家。黑豆、 菜豆、 物种的一种豆类植物通常以罐装或干形式购买。1 杯干豆产量约 2 杯煮熟的豆。黑豆汤 (内格罗斯所帕 de frijoles) 是另一个常用准备古巴喜爱。');
insert into Foods_CN(Food_Title,CName,Description) values('Pinto bean','平托豆','平托 bean 是各种常见豆 （菜豆）。这是最常见的 bean 在墨西哥西北部与美国  并且是最经常吃整个的肉汤或捣碎和 refried。整个或捣碎，它是共同馅的面卷饼。也可能收获年轻豆荚和将其作为绿色斑豆煮熟。');
insert into Foods_CN(Food_Title,CName,Description) values('Shaved ice','刨冰','刨冰是基于冰甜点做的细屑的冰或精巧碎的冰和甜调味品或糖浆的一个大家族。通常，糖浆加冰冻结剃光后 — — 通常在销售终端。但是，也可以在冻结前添加调味。甜品是消耗的世界范围内的各种形式和礼仪。刨冰还可以与大量的液体产生的刨冰饮料混合了。');
