insert into Foods_CN(Food_Title,CName,Description) values('Boiling-point elevation','沸点高程','沸点海拔描述，当复合添加另，意味着解决方案具有较高的沸点比纯溶剂 （溶剂型） 液体的沸点会更高的现象。每当非挥发性溶质，如一种盐，添加到纯溶剂，如水，将发生这种情况。沸点可以准确地用糖度计来衡量。总之，因此，沸点海拔意味着当溶质溶解在溶剂中，生成的解决方案的沸点比纯溶剂的高。');
insert into Foods_CN(Food_Title,CName,Description) values('Salt','盐','盐是氯化钠的一种矿物物质组成主要 (NaCl)，一种化合物属于较大类的离子盐 ；作为一种结晶的矿物称为盐岩或岩盐盐以其自然的形式。盐是哪里固体每公升，盐度为 3.5%的主要矿物成分，与大海有约 35 克 (1.2 盎司) 海中大量存在。盐是动物生命的根本，咸味是基本的人类口味之一。动物的组织包含较大数量的盐比做植物组织 ；因此典型的人以他们的羊群和牛群为生的游牧民族饮食要求很少或不加的盐，而谷物基础日粮需要补充。盐是最古老和最广泛的食品调味料之一和盐是一种重要的食品保存方法。');
insert into Foods_CN(Food_Title,CName,Description) values('Cooking','烹饪','烹煮或烹饪是艺术的制备与使用消费热的食物。烹饪技术和成分差异很大在整个世界，反映出独特的环境、 经济和文化传统和发展趋势。那烹饪发生的方式还取决于技能和培训个别厨师的类型都有。烹饪还可发生化学反应不在场的热量，最显著的是与腌鱼，鱼烹煮的柠檬汁或酸橙汁中的酸的传统美国南方菜。寿司也使用类似鱼和水稻上釉与醋的酸性内容之间的化学反应。');
insert into Foods_CN(Food_Title,CName,Description) values('Glossary of chess','象棋术语表','本页面说明常用的术语，在按字母顺序排列的棋。其中一些有他们自己的页面，像叉和 pin。非正统的棋子的列表，请参见童话棋子 ；国际象棋问题的专用术语的列表，请参见词汇表棋的问题;象棋相关游戏的列表，请参阅棋变形。');
insert into Foods_CN(Food_Title,CName,Description) values('Cooked mode','熟的模式','熟的模式正在给一个程序之前进行的数据预处理的 Unix 系统中是终端或伪终端字符设备的模式。在此模式下，系统将解释退格、 删除和控制 C 和控制 D.等其他控制字符等特殊字符什么构成一个煮熟的模式的精确定义是特定于操作系统。 另一种模式是"原始模式"，也被称为 delimiterless 的输入，在其中的数据考虑作为-是到该程序，并在系统不解释任何特殊字符。');
insert into Foods_CN(Food_Title,CName,Description) values('Boiling','沸腾','沸腾是液体的液体，当一种液体被加热到它的沸点的蒸气压力等于受到压力，液体通过对周围环境的压力，温度时发生的快速蒸发。');
insert into Foods_CN(Food_Title,CName,Description) values('Boiled egg','水煮的蛋','水煮的蛋是鸡蛋 (通常鸡卵) 在与他们的炮弹不间断沸水中浸煮熟。(没有它们的壳水中煮熟的鸡蛋被称为煮鸡蛋，同时低于煮沸的温度，带或不带外壳，煮熟的鸡蛋被称为娇生惯养鸡蛋。)要么煮足够长的卵白，然后蛋黄凝固，或他们是剩热水，冷静下来，这将逐渐固化它们，同时一个半熟的鸡蛋蛋黄，有时甚至至少部分液体的白色，仍然是鸡蛋。');
insert into Foods_CN(Food_Title,CName,Description) values('Boiled leather','水煮的皮革','水煮的皮革，有时被称为皮色红烧肉是历史建设材料的盔甲。它包括厚皮革、 煮沸的水中。据一些消息来源，水煮油和蜡方面使用了，而其他人断定发酵动物尿液中氨的使用。沸腾导致皮革的但也更脆更难。水煮的皮可以做成跛脚或秤来使层状或缩放的盔甲。皮革灵活短时间后仍然沸腾，从而使其可以被制成较大的盘子。');
insert into Foods_CN(Food_Title,CName,Description) values('Burrito','墨西哥卷饼','墨西哥卷饼 （美国英语/bəˈritoʊ/，buˈrito (help·info))， 是一种类型的墨西哥裔美国食品。它包括包裹或折叠成一个圆柱形状完全括起填补小麦面粉玉米粉圆饼。（相比之下，玉米面豆卷通常被形成通过简单地在周围填充，在打开的半圆的周长的一半折叠玉米粉圆饼。)面粉玉米饼通常轻轻烤或蒸，软化它并使它更柔韧。在墨西哥，肉和炸豆子有时是唯一的馅料。');
