insert into Foods_CN(Food_Title,CName,Description) values('Vegetarian cuisine','素食菜','素食菜是指满足素食主义者的标准，它不包括肉类和动物组织产品的食品。为 lacto ovo 素食主义 (素食主义在西方世界中的最常见类型），鸡蛋和牛奶和奶酪等奶制品被允许。奶素食主义，为已知最早的素食主义 (在印度录制)、 牛奶、 奶酪等乳制品类型被允许。 的严格形式的素食主义素食主义和 fruitarianism，排除所有的动物产品，包括乳制品，以及蜂蜜，，甚至一些精制糖如果筛选和美白与骨炭。');
insert into Foods_CN(Food_Title,CName,Description) values('Chickpea','鹰嘴豆','(鹰嘴豆) 鹰嘴豆是豆科豆科，亚科 Faboideae 的家庭。它的种子很高的蛋白质。它是最早的种植豆科牧草之一： 7,500 岁遗骸被发现在中东地区。 1');
insert into Foods_CN(Food_Title,CName,Description) values('Gram flour','克面粉','克面粉，也称为鹰嘴豆面粉、 鹰嘴豆面粉或 besan，是脉冲粉的地面鹰嘴豆。在许多国家中使用，是在印度、 巴基斯坦、 尼泊尔和孟加拉美食主食成分。克面粉可从原始的鹰嘴豆或烤鹰嘴豆。烤的品种就更加美味，在原料品种有略有苦涩味。 1');
insert into Foods_CN(Food_Title,CName,Description) values('Arab cuisine','阿拉伯菜','阿拉伯菜被定义为不同区域的菜式跨越阿拉伯世界，从美索不达米亚到沙特阿拉伯，并纳入岩画、 埃及人和其他传统。');
insert into Foods_CN(Food_Title,CName,Description) values('Lysine','赖氨酸','赖氨酸 (简称 Lys 或 K）  是与化学公式 HO2CCH(NH2) (CH2) 4NH2 α-氨基酸。它是一种基本的氨基酸为人。AAA 和 AAG 赖氨酸的密码子。');
insert into Foods_CN(Food_Title,CName,Description) values('Poppy seed','罂粟种子','罂粟籽是从鸦片罂粟 (罂粟) 获得油料。微小的肾形种子收获后从干的种子荚各种文明几千年。使用种子，整个或地面，作为一种在许多食物成分和他们按产量 poppyseed 油。');
