insert into Foods_CN(Food_Title,CName,Description) values('Canning','福康宁','福康宁是保存的食物处理和密封在密闭容器中的食物内容的方法。福康宁提供货架期通常从一到五年，虽然在特定情况下它可以更长的时间。冻干的罐头的产品，如罐头干小扁豆，在食用的状态可能会持续长达 30 年。1974 年，由全国食品加工者协会测试是从伯特兰在 1865 年，下沉了汽船击毁的罐头食品样品。虽然外观、 气味和维生素含量已经恶化，没有发现微生物的生长和食物被确定为仍安全食用。 1');
insert into Foods_CN(Food_Title,CName,Description) values('Canned fish','鱼罐头','罐头的鱼就是鱼，处理，在密闭容器里密封罐头，如密封并受到热。福康宁是一种保存食物的方法，并提供典型货架期从一年至五年不等。');
