insert into Foods_CN(Food_Title,CName,Description) values('Tofu','豆腐','豆腐，也称为豆腐是凝固的豆浆和成软白块然后按结果酿三宝所作的一种食品。它是许多东亚和东南亚菜肴中的一个组件。 有许多不同种类的豆腐，以某种方式包括鲜豆腐和已处理的豆腐。豆腐一微妙的风味，可以在咸味和甜味菜肴中使用。它往往是经验丰富或腌制适合这道菜。');
