insert into Foods_CN(Food_Title,CName,Description) values('Beer in Scotland','在苏格兰的啤酒','啤酒在苏格兰制作大约 5000 年。 利用苦味草药的凯尔特传统仍然在苏格兰长比欧洲其他国家。大多数啤酒厂开发了在中央的平地，还载有人口的主要中心。爱丁堡和阿洛亚特别是成为了注意到的中心的出口在世界各地的啤酒。在二十世纪结束时，小啤酒厂，苏格兰各地出现了。');
insert into Foods_CN(Food_Title,CName,Description) values('Low-alcohol beer','低醇啤酒','低醇啤酒 （也称为淡啤酒、 无酒精啤酒、 小啤酒、 小麦芽酒或啤酒附近） 是啤酒的低酒精含量或没有酒精，目的是要重现没有败坏影响的标准酒精酿造啤酒的口味。大多数低醇啤酒是啤酒，但是有一些低酒精啤酒。');
insert into Foods_CN(Food_Title,CName,Description) values('Table wine','表酒','表酒是葡萄酒的术语，用两个不同的意思： 一种样式的葡萄酒和葡萄酒分类内的质量水平。');
insert into Foods_CN(Food_Title,CName,Description) values('Wine','红酒','酒是由发酵的葡萄或其他水果制成的酒精饮料。葡萄的天然化学平衡允许它们发酵不添加糖、 酸、 酶、 水或其它营养成分。 酵母消耗葡萄中的糖分，将它们转换成酒精和二氧化碳。不同品种的葡萄和株酵母菌产生不同风格的红酒。知名的变化导致从之间的生化发展水果、 参与发酵、 风土和随后的称谓，并在整个过程中人为干预的反应十分复杂的相互作用。');
insert into Foods_CN(Food_Title,CName,Description) values('Wine table','酒表','酒-表是促进饭后喝一晚 15 世纪装置、 cabinetmakers 称之为"绅士的社会表"。它始终是狭隘和的半圆或马蹄形式和客人坐在圆形的外圆周。较早和较简单的形状中金属井瓶和冰沉没在表面的表 ；他们都配备黄铜盖子。');
