insert into Foods_CN(Food_Title,CName,Description) values('Sharbat','Sharbat','Sharbat 或冰霜 (阿拉伯语： شربات Sharbat ；波斯语/旁遮普语/乌尔都语： شربت Sharbat ；孟加拉文 শরবত Shorbot ；土耳其语： Şerbet ；Azerbaijani:Şərbət） 是最受欢迎的西部和南部亚洲饮料，准备从水果或花瓣。 它是甜和冷冻后饮用。它可以以浓缩的形式送达和用勺子吃或用来创建喝的水稀释。');
