insert into Foods_CN(Food_Title,CName,Description) values('Instant messaging','即时消息','即时消息 (IM) 是一种类型的在线聊天，在互联网上提供实时文本传输。局域网信使局域网的运作以类似的方式。两党之间，当每个用户选择完成思想，并选择"发送"时通常双向传输短消息。一些 IM 应用程序可以使用推送技术，提供实时文本，传输消息字符，因为它们是由组成。更高级的即时消息可以添加文件传输，可单击超链接，语音 IP 或视频聊天。');
insert into Foods_CN(Food_Title,CName,Description) values('Instant noodle','方便面','方便面是干或调理的面条块，通常卖用调味粉和/或调味油的数据包。挂面后通常是吃的煮或浸泡在沸水中，虽然可以加热或直接从该数据包吃调理的面条。方便面是由日本日清食品百福安藤发明的。 他们发起了 1958 金拉面的品牌名称。1971 年，日清介绍了杯面，在聚苯乙烯杯子的挂面块。');
insert into Foods_CN(Food_Title,CName,Description) values('Morangos com Açúcar','Morangos com Açúcar','Morangos com Açúcar (用葡萄牙语，甜草莓作为国际上分发糖草莓） 是葡萄牙青年戏剧。它是于加拿大 tvi 集团之间 2003 年 8 月 30 日至 2012 年 9 月 15 日，葡萄牙电视台每日播出。它也是在安哥拉、 叙利亚、 巴西和罗马尼亚广播。');
insert into Foods_CN(Food_Title,CName,Description) values('Sugar','糖','糖是甜、 短链、 可溶性碳水化合物，其中许多在食品中使用的广义的名称。他们是碳水化合物，由碳、 氢和氧组成。有各种类型的不同来源的糖。单糖被称为单糖的组成，包括 (也称为葡萄糖) 葡萄糖、 果糖和半乳糖。表或作为食物最惯常使用的砂糖是蔗糖、 双糖。（在正文中，蔗糖酶入果糖和葡萄糖)。其他双糖包括麦芽糖和乳糖。较长的糖链称为低聚糖。不同化学物质也可能有清甜的味道，但不是作为糖分类。一些被用作描述为人工甜味剂的糖的热量较低食品替代品。');
