insert into Foods_CN(Food_Title,CName,Description) values('List of soft drinks by country','软性饮料的国家的名单','这是原籍的软性饮料的品牌国的顺序的列表。软性饮料是一种饮料，它通常包含 （通常，但不是总是，碳酸水） 水、 通常一种甜味剂和通常调味剂。这种甜味剂可能是糖、 高果糖玉米糖浆、 果汁、 糖代用品 （如饮食饮品) 或这些项的组合。软性饮料还可能包含咖啡因、 色素、 防腐剂等成分。');
insert into Foods_CN(Food_Title,CName,Description) values('Cooking plantain','烹饪车前草','烹饪车前草或车前草 (/ ˈplæntɨn /  属其果实打算只后烹煮或其他消耗的穆萨的栽培品种 (品种) 之一处理，而不被吃掉的原料。拍摄还用于使食物和汤在各种美食，还使用了树叶和纤维。');
insert into Foods_CN(Food_Title,CName,Description) values('Vitamin C','维生素 C','维生素 C 或 L-抗坏血酸或只是抗坏血酸 (抗坏血酸的阴离子），是为人和某些其他动物物种的重要营养素。维生素 C 是指数量的动物，包括抗坏血酸和及其盐中有维生素 C 活动的 vitamers 和一些氧化形式的像 dehydroascorbic 酸分子。抗坏血酸和抗坏血酸都在正文中自然存在时这些对象之一引入细胞，因为窗体 interconvert 根据 ph 值。');
