insert into Foods_CN(Food_Title,CName,Description) values('Brennivín','Brennivín','Brennivín (冰岛语的发音： ) 是明确的、 不加糖的蒸馏酒，被认为是冰岛的签名蒸馏饮料。它是最受欢迎的冰岛酒和射击，特别场合酒精和 Þorrablót 的冬季中期盛宴的传统饮料。它是由发酵的谷物或马铃薯饲料和香菜、 小茴香、 当归和其他的植物成分特色风味。浸泡草药中创建杜松子酒的酒精是斯堪的纳维亚国家长期持有的民俗传统。Brennivín 味道独特的具有类似于伏特加或斯堪的纳维亚白兰地。这通常是在 80 证明瓶装的。');
