insert into Foods_CN(Food_Title,CName,Description) values('Energy drink','能量饮料','能量饮料是一种类型的饮料含有刺激性药物，主要是咖啡因，作为提供心理和生理刺激销售。他们可能或不可能碳酸，许多人还包含糖或其他甜味剂、 草本精华和氨基酸。他们是一个大集团的能源产品，其中包括酒吧和凝胶的子集。有很多的品牌和品种的能量饮料。');
